mapper.select:
SELECT A.* 
	FROM (SELECT ROWNUM RN, D.* 
			FROM (SELECT * FROM DIARYBOARD
	               WHERE DDATE = #{ddate} AND MID = #{mid}
	               ORDER BY DRDATE DESC) D ) A
	WHERE RN BETWEEN #{startRow} AND #{endRow}

		SELECT COUNT(*)
    		FROM DIARYBOARD
    		WHERE DDATE = #{ddate} AND MID = #{mid}

SELECT D.*, MNAME 
    FROM DIARYBOARD D, MEMBER M
    WHERE D.MID=M.MID AND DNUM = #{dnum}
